import React, { useEffect, useState, ChangeEvent, FormEvent } from 'react'
import './styles.css'
import { Link, useHistory } from 'react-router-dom'
// import { FiArrowLeft } from 'react-icons/fi'
import api from '../../services/api'
import InputMask from 'react-input-mask';
import  Sidebar  from '../../components/sidebar/Sidebar'
import Submenu from '../../components/sidebar/Sidebar'

const CreatePoint = () => {

    const [initialPosition, setInitialPosition] = useState<[number, number]>([0, 0])

    const [formData, setFormData] = useState({

        name: '',
        examDate: '',
        examDuration: '',
        examDescription: '' 

    })


    const [selectedPosition, setSelectedPosition] = useState<[number, number]>([0, 0])
    const [selectedItems, setSelectedItems] = useState<number[]>([])
    const history = useHistory()

     function handleInputChange(event: ChangeEvent<HTMLInputElement>) {
        const { name, value } = event.target

        setFormData({ ...formData, [name]: value })
    }

    async function handleSubmit(event: FormEvent) {
        event.preventDefault()

        const { name,
                examDate,
                examDuration,
                examDescription } = formData;


        const data = {
            name,
            examDate,
            examDuration,
            examDescription
        }

        console.log(data);


        await api.post("/users", data)
            .then(response => {
                console.log(response);
            })
            .catch(error => {
                console.log(error);
            });
        history.push('/create-point')

        alert("Exam Created")

    }



    return (
        <div id="page-create-point">
            <Sidebar>


            </Sidebar>

            <Submenu />

            <form onSubmit={handleSubmit}>
                <h1> Examinations</h1><br />
                <p>Please, fill in all the informations</p> <br />
                <p>Obligatory *</p>

                <fieldset>

                    <div className="field">
                        <label htmlFor="title">Patient name: *</label>

                        <input
                            type="text"
                            name="name"
                            id="name"
                            onChange={handleInputChange}
                            required
                        />
                    </div>

                    <div className="field">
                        <label htmlFor="description">Examination Date: *</label>

                        <InputMask mask="99/99/9999"
                            type="text"
                            name="examDate"
                            id="examDate"
                            onChange={handleInputChange}
                            required
                        />
                    </div>

                    <div className="field">
                        <label htmlFor="description">Examination Duration: * </label>

                        <input
                            type="text"
                            name="examDuration"
                            id="examDuration"
                            onChange={handleInputChange}
                            width="4800px"
                            required
                        />
                    </div>

                    <div className="field">
                        <label htmlFor="description">Medical exam description: *</label>

                        <input
                            type="text"
                            name="examDescription"
                            id="examDescription"
                            onChange={handleInputChange}
                            required
                        />
                    </div>


                </fieldset>

                <button type="submit">
                    Forward exam
                </button>

            </form>

        </div>
    )

}

export default CreatePoint;
