import Api from '../../services/api';
import {useParams, Link} from 'react-router-dom';
import axios from 'axios';
import Button from '../../components/utils/button/button';

export default function EnableProfile(){

    const {idUsuario} = useParams();

    async function handleSubmmit(e){
        
        e.preventDefault()

        
    
    try {
    window.confirm('Você tem certeza?')
    axios({
        method:'PATCH',
        url:`http://localhost:8081/enable/${idUsuario}`,
        headers:{
            'Authorization':localStorage.getItem('token')
        }
    })
    localStorage.clear();
    window.location.href='/'
    
    } catch (error) {
        const { data } = error.response;
        alert(data.message); 
    }
    
    
        
    }
    return(
        <div>
             <div><a href="/home"><img src="https://i.imgur.com/tDvwyyA.png" className = {'logo'} title="Smart Palm"/></a></div><br></br><br></br>
            <form className={"form"}>
            <text className ={"title"}>Desativar Perfil</text><br></br><br></br>
            <text className = {"subtitle"}>Ao desativar o seu perfil, você não terá mais acesso ao sistema Smart Palm. Você tem certeza de que deseja prosseguir?</text>
            <br></br><br></br>
            <Link to = '/justification'><Button onClick={handleSubmmit}>Desativar Conta</Button></Link>
            </form>
        </div>
    )
}
