import { useState} from "react";
import Api from "../../services/api";
import {useParams} from 'react-router-dom';
import Button from '../../components/utils/button/button';

export default function Justification(){

   const [name, setName] = useState('');
   const [description, setDescription] = useState('');

   const {idUsuario} = useParams();
   
   async function handleSub(e){

    e.preventDefault()
        

        const config = {
            headers:{
                Authorization:localStorage.getItem('token')
        }

    }
        try {
            
        if(name!==''&&description!==''){
           const res =  await Api.post('justification',{name, description}, config)
            console.log("res>>>>>>", res.data);
            alert('Obrigado pelo feedback!')
            window.location.href=`/enableProfile/${idUsuario}`
            
       //{/window.confirm("Tem certeza?");/}
        }else{
            alert('Preencha todos os campos')
        }
        
        } catch (error) {
            console.log(">>>>>ERRO>>>>",error)
        }
       
   }



    return(
        <div>
            <form className={"form"} >
            <text className={"subtitle"}>Justifique sua sa√≠da</text>
                <br></br><br></br>
                <div className={"question"}>
                <input name="name" id="name" type="text" required
                    onChange={e => setName(e.target.value)}
                    />
                <label>Name:</label>
             </div>
             <div>
                 <input type="checkbox"/><span></span>
             </div>

                <br></br>
                <textarea className = {"textarea"} onChange={e => setDescription(e.target.value)} placeholder="Sua justificativa" rows="7" cols="77"/>
                <div>
                    <br></br>
                <Button  onClick={handleSub}>Enviar</Button>
            </div>
            </form>
        </div>
    )
}
