import React from 'react';
import Botao from './botao';
export default class Clock extends React.Component {

    constructor (){
        super();
        this.state={
          contador:60,
          estado:"Iniciar"
        }
        this.handleContadorplus= this.handleContadorplus.bind(this);
        this.handleContadorminus= this.handleContadorminus.bind(this);
        this.aumentar= this.aumentar.bind(this);
      }
    
      handleContadorplus(){
        if (this.state.estado==="Iniciar"){
          this.setState({
            estado: "Pausa"
          });
          this._interval=setInterval(this.aumentar,1000);
          }
        else if (this.state.estado==="Pausa"){
            this.setState({
              estado: "Iniciar"
          });
          clearInterval(this._interval);
         
        }
    
      }
      aumentar(){
        if(this.state.estado==="Pausa"){
          this.setState({
            contador: this.state.contador-1
          });
        }
      }
      handleContadorminus(){
        this.setState({
          contador: 0,
          estado:"Iniciar"
        });
        clearInterval(this._interval);
      }
      render() {
        return (
          <div className=" text-center">
    
            <h1>{this.state.contador}</h1>
            <Botao text={this.state.estado}   onClick={this.handleContadorplus}/>
            <Botao text="Detener"  onClick={this.handleContadorminus}/>
            <Botao text="outro"  onClick={this.aumentar}/>
          </div>
        );
      }
    }
