import { useState} from "react";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import InputMask from 'react-input-mask';
import '../register/style.css';
import Button from '../../components/utils/button/button';
import Api from "../../services/api";
import { Formik, Field, Form } from 'formik';
toast.configure();

export default function Register() {

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [crm, setCrm] = useState('');
  const [cpf, setCpf] = useState('');
  const [phone, setPhone] = useState('');
  const [specialization, setSpecialization] = useState('');
  
  
  async function submitOn(e) {
    
    e.preventDefault();
    const data = {
      name: name,
      email: email,
      password: password,
      crm: crm,
      cpf: cpf,
      phone: phone,
      specialization: specialization
    }
    try {
      if(name!==''&&email!==''&&password!==''&&crm!==''&&phone!==''&&specialization!==''){ 
          
        
        
        
        const response = await Api.post('verifyUser',data)
        if(response.status === 201){
          sessionStorage.setItem('user',JSON.stringify(data));
          window.location.href= '/confirmation';
        }
      }else{
        alert("Fill in all data")
      }
    
    } catch (error) {
      const { data } = error.response;
      alert(data.message);
      
    }
        
      
    
}
  
  

function onblurCrm(e,setFieldValue){

  const {value} = e.target;

  if(value === ''){
    return;
  }
  fetch(`https://www.consultacrm.com.br/api/index.php?tipo=crm&uf=am&q=${value}&chave=2798018964&destino=json`)
  .then((res) => res.json())
  .then((data) =>{
    
    console.log(setFieldValue(data.item))
  })
  
}
return (
  <div >
    
  <div><a href="/"><img src="https://i.imgur.com/tDvwyyA.png" className={'logo2'} title="source: imgur.com" /></a></div>
    <Formik
      onSubmit={submitOn}
      validateOnMount
      initialValues={{
        name: '',
        specialization: '',
        crm: '',
        
      }}
      render={({ isValid, setFieldValue }) => (
        <Form>
          <h1 className='title'>Register</h1>
      <h2 className='subtitle'>Fill in the information below</h2>
      <div className = {"question"}>
      <input name="name" id="name" type="text" required
          onChange={e => setName(e.target.value)}
        />
        <label>Name:</label>
      </div>
      <br></br>
      <div className = {"question"}>
        <InputMask mask="999.999.999-99" name="cpf" type="text" id="cpf" required

          onChange={e => setCpf(e.target.value)}
        />
        
        <label>CPF:</label>
      </div>
      <br></br>
      <div className={"question"}>
        <InputMask mask="9999" id="crm" name="crm" type="text" required
          
          onBlur={onblurCrm}
        />
       <label>CRM:</label>
      </div>
      <br></br>
      <div className={"question"}>
        <input   name="specialization" id="specialization" type="text" required
          
        />
        <label>Specialization:</label>
      </div>
      <br></br>
      <div className={"question"}>
        <InputMask mask="(99) 99999-9999" id="phone" name="phone" type="text" required
          onChange={e => setPhone(e.target.value)}
        />
        <label>Phone:</label>   
      </div>
      <br></br>
        <div className={"question"}>
          <input name="email" id="email" type="email" required
          onChange={e => setEmail(e.target.value)}
           />
          <label>E-mail:</label>
        </div>
        <br></br>
        <div className={"question"}>
          <input mask="password" id="password" name="password" type="password" required

          onChange={e => setPassword(e.target.value)}
        />
        <label>Password:</label>
      </div>
      <br></br>
      <div>
      
      <Button>Confirm</Button>
    </div>  
        </Form>
      )}
    />
  </div>
);
}
