import { useState} from "react";
import Api from "../../services/api";
import {useParams} from 'react-router-dom';
import Button from '../../components/utils/button/button';

export default function Justification(){

   const [name, setName] = useState('');
   const [teste1, setTeste1] = useState('');
   const [teste2, setTeste2] = useState('');
   const [teste3, setTeste3] = useState('');
   const [teste4, setTeste4] = useState('');
   const [description, setDescription] = useState('');

   const {idUsuario} = useParams();
   
   async function handleSub(e){

    e.preventDefault()
        

        const config = {
            headers:{
                Authorization:localStorage.getItem('token')
        }

    }
        try {
            
        if(name!==''&&description!==''){
           const res =  await Api.post('justification',{name, description}, config)
            console.log("res>>>>>>", res.data);
            alert('Obrigado pelo feedback!')
            window.location.href=`/enableProfile/${idUsuario}`
            
       //{/window.confirm("Tem certeza?");/}
        }else{
            alert('Preencha todos os campos')
        }
        
        } catch (error) {
            console.log(">>>>>ERRO>>>>",error)
        }
       
   }



    return(
        <div>
            <form className={"form"} >
            <text className={"subtitle"}>Justifique sua saída</text>
                <br></br><br></br>
                <div className={"question"}>
                <input name="name" id="name" type="text" required
                    onChange={e => setName(e.target.value)}
                    />
                <label>Name:</label>
             </div>
             <center>
            <br></br>
             <input name="teste1"  onChange={e=>setTeste1} type="checkbox"/>Não usarei mais a plataforma&nbsp;&nbsp;
             <input name="teste2" onChange={e=>setTeste2} type="checkbox"/>Não atendeu as minhas necessidades&nbsp;&nbsp;
             <input name="teste3" onChange={e=>setTeste3} type="checkbox"/>Achei a plataforma confusa ou difícil&nbsp;&nbsp;
             <input name="teste4" onChange={e=>setTeste4} type="checkbox"/>Simplemente, não gostei da plataforma&nbsp;&nbsp;
             <input name="teste5"  type="checkbox"/>Não consegui utilizar as funcionalidades&nbsp;&nbsp;
             <input name="teste6" type="checkbox"/>Outros motivos&nbsp;&nbsp;
             </center>
                
                
                <div>
                    <br></br>
                <Button  onClick={handleSub}>Enviar</Button>
            </div>
            </form>
        </div>
    )
}
