import React from 'react';
import axios from 'axios';
import Botao from './botao';
import Api from '../../../../services/api';
export default class Clock extends React.Component {

    constructor (){
        super();
        this.state={
          contador:60,
          estado:"Confirmar e-mail",
          
        }
        
        this.handleContadorplus= this.handleContadorplus.bind(this);
        this.handleContadorminus= this.handleContadorminus.bind(this);
        this.aumentar= this.aumentar.bind(this);
      }
    
      
      async handleContadorplus(e){
        e.preventDefault();
        const email =  sessionStorage.getItem('emailValidator');
       
        await Api.post('sendEmailController',{email})
         
        if (this.state.estado==="Confirmar e-mail"){
          this.setState({
            estado: "LOADING..."
          });
          this._interval=setInterval(this.aumentar,1000);
          }
        else if (this.state.estado==="LOADING..."){
            this.setState({
              estado: "Confirmar e-mail"
          });
          clearInterval(this._interval);
         
        }
    
      }
      aumentar(e){
        
        if(this.state.estado==="LOADING..."){
          this.setState({
            contador: this.state.contador-1
            
          });
          
          if(this.state.contador === 0){
            window.location.href= '/';
        }
        }
      }
      async handleContadorminus(){
        const email = sessionStorage.getItem('emailValidator');
        const j = JSON.parse(email);
       
        console.log("enviando email...",j.email);
        
        try {
          const response = await Api.post('sendEmailController',email);
          if(response.status === 200){
            alert('Seu link foi reenviado!');
            window.location.href= '/';
            sessionStorage.clear();
          }
        } catch (error) {
          const { data } = error.response;
          alert(data.message);
        }
       
      }
      
      render() {
        
        return (
          <div className=" text-center">
    
            <h1>{this.state.contador}</h1>
            <Botao  text={this.state.estado}   onClick={this.handleContadorplus}/>
            <Botao text="Reenviar"  onClick={this.handleContadorminus}/> 
          </div>
        );
      }
    }
