import Button from '../../pages/login/buttonLogin/Button';
import './index.css';
import { useState} from "react";
import Api from '../../services/api';

export default function SignIn() {

  const [ email, setEmail ] = useState('');
  const [ password, setPassword ] = useState('');
 

  async function handleSubmit(){
   
    
    try {
      if(email!==''&&password!==''){
        await Api.post('signInDoctor',{email, password})
    .then(response =>{
      if(response.status === 200){
        localStorage.setItem('token', response.data.token)
        window.location.href='/home'
      }else if(response.data.status === 404){
        alert('Este usuário não existe ou está inativo')
        window.location.href='/'
      }else{
        alert('Erro no servidor')
        window.location.href='/'
      }
    })
      }else{
        alert('Por favor, preencha todos os dados!');
      }
      
    } catch (error) {
      alert("E-mail ou senha inválidos, verifique se suas credenciais são válidas!")
      window.location.href='/'
    }

    
}


return (
  <div>
  <div><a href="/home"><img src="https://i.imgur.com/tDvwyyA.png" className = {'logo'} title="Smart Palm"/></a></div>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <br></br>
  <form className={'form'}>
  <text>Start your login</text>
    <br></br>
    <br></br>
    <br></br>
    <label>
      E-mail:
      <input type="text" name="Email" onChange={e => setEmail(e.target.value)} />
    </label>
    <label>
      Password:
      <input type="password" name="password" 
        onChange={e => setPassword(e.target.value)}/>
    </label>
    <br></br>
    <Button buttonSize='btn--large' onClick={handleSubmit}>Sign in</Button>
    <br></br>
    <br></br>
    <a href = "/forgotPassword" className={"label1"}>I forget the password</a>
    <a href ="/register" className={"label2"}>Sign up</a>
   </form>
  
</div>
);
}
