import Button from "./buttonEdit/Button";
import {useParams} from 'react-router-dom';
import {useState,useEffect}from 'react';
import Api from '../../services/api';

export default function EditProfile() {

    const [specialization, setSpecialization] =  useState('');
    const [phone, setPhone] =  useState('');

    const {idUsuario} = useParams();
    
    useEffect(() => {
        async function getUsuario(){
          var response = await Api.get('/api/usuarios.details/'+idUsuario);
          
          setSpecialization(response.data.specialization);
          setPhone(response.data.phone);
        }
    
        getUsuario();
      },[])

async function handleSubmit(){
    const data = {
        specialization:specialization,
        phone:phone,}


    const config = {
        headers:{
            Authorization:localStorage.getItem('token')
    }
    
}
   if(specialization!==''&&phone!==''){
        const response = await Api.put('updateUser/',data);

        if(response.status===200){
          window.location.href='/profile'
        }else{
          alert('Erro ao atualizar o usuário!');
        }
      }else{
        alert('Por favor, preencha todos os dados!');
      }

}
    
        return(
            <div>
                <h2>Tela de editar perfil</h2>
                <br/>
                <div>
                    <form className="form">
                    <text>Editar Perfil</text>
                    <br/>
                        <label>
                            Especificação:
                            <input type="text" 
                            onChange={e => setSpecialization(e.target.value)}/>
                        </label>
                        <br/>
                        <label>
                            Telefone:
                            <input type="text" 
                            onChange={e => setPhone(e.target.value)}/>
                        </label>
                        <div>
                        
                        <Button onClick={handleSubmit} buttonSize='btn--large' type="submit">Editar</Button>
                        </div>
                    </form>
                    
                </div>
            </div>
        )
}
