import React,{useState,useEffect} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import Api from '../../../../services/api';

export default function FormDialog() {
  const [open, setOpen] = React.useState(false);
  const [email, setEmail] = useState('');

  useEffect(() => {
    async function jsonUser(){
      const  user = sessionStorage.getItem('emailValidator');
      setEmail(user);
    }
    jsonUser();
  },[])


  async function handleClickOpen (e) {

    try {
      //Pega o dado do local storage em arquivo bruto("fulano@gmail.com")
  const emailExists = sessionStorage.getItem('emailValidator')
  //console.log("email COM PARSE",JSON.parse( emailExists))
 
  //Transforma em objeto
  
  //Transforma a string em objt (JSON.parse(emailExists))
  const email =JSON.stringify(emailExists);
  const data ={
    email:emailExists
  }
   //Manda via post esse dado para a api {"email":"fulano@gmail.com"}
   const response = await Api.post('findEmail/',data);
   if(response.status === 200){
    //Se ok retorna 200 e o id específico daquele email
    console.log(">>>>>>>>",response.data.id)
    //Guardo o id no sessionStorage
    sessionStorage.setItem('id',response.data.id);
    //Busco esse id
    const dataId2 = sessionStorage.getItem('id');
    console.log("id>",dataId2)
    window.location.href= '/confirmEmail';
  }
    } catch (error) {
      //Retorno erros da api
      const { data } = error.response;
      alert(data.message);
    }

    setOpen(true);
  };

  async function okHandle (e) {
    e.preventDefault();
    try {
    //Seto esse novo valor no sessionStorage e converto para string
    sessionStorage.setItem('emailValidator', JSON.stringify(email))
    //Busco esse novo valor
    const b = sessionStorage.getItem('emailValidator');
    console.log("newEmail",email)
    console.log("b",b)
    //Busco o id daquele email que retorna do banco de dados
    const dataId = sessionStorage.getItem('id');
    //Passo o novo email no body e o id dentro da requisição PUT updateMailSend/{id}
    await Api.put(`updateMailSend/${dataId}`, {email});
    } catch (error) {
      //Retorno erros da api
      const { data } = error.response;
      alert(data.message);
    }
    
    setOpen(false);
  };
 

  return (
    <div>
      <Button fullWidth variant="contained" color="secondary" onClick={handleClickOpen}>
      To edit
      </Button>
      <Dialog open={open} type="email" onClose={okHandle} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">To edit</DialogTitle>
        <DialogContent>
          
          <TextField
            autoFocus
            margin="dense"
            id="name"
            value={email}
            onChange={e => setEmail(e.target.value)}
            label="Email"
            type="email"
            require
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          
          <Button
          
          onClick={okHandle} color="secondary">
            ok
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
