import {useParams} from 'react-router-dom';
import {useState}from 'react';
import Api from '../../services/api';
import Input from '../../components/utils/regex/input';

export default function ProfilePerfil() {

    const [specialization, setSpecialization] =  useState('');
    const [phone, setPhone] =  useState('');

    const {idUsuario} = useParams();
    
    

async function handleSubmit(){
    const data = {
        specialization:specialization,
        phone:phone,}


    const config = {
        headers:{
            Authorization:localStorage.getItem('token')
    }
    
}
   if(specialization!==''&&phone!==''){
        const response = await Api.put(`updateUser/${idUsuario}`,data, config);

        if(response.status===200){
          window.location.href='/profile'
        }else{
          alert('Erro ao atualizar o usuário!');
        }
      }else{
        alert('Por favor, preencha todos os dados!');
      }

}
    
        return(
            <div>
                <h2>Tela de editar perfil</h2>
                <br/>
                <div>
                    <form >
                    <text>Editar Perfil</text>
                    <br/>
                        <label>
                            Especificação:
                            <input type="text" 
                            onChange={e => setSpecialization(e.target.value)}/>
                        </label>
                        <br/>
                        <label>
                            Telefone:
                            <Input className = {'input'} mask="telefone" id="phone" 
                             name="phone"
                            onChange={e => setPhone(e.target.value)}/>
                        </label>
                        <div>
                        
                        <button onClick={handleSubmit}type="submit">Editar</button>
                        </div>
                    </form>
                    
                </div>
            </div>
        )
}
