import { useState} from "react";
import Api from "../../services/api";


export default function Justification(){

   const [name, setName] = useState('');
   const [justify, setJustify] = useState('');

   async function handleSub(e){


        const data={
            name:name,
            justify:justify
        }

       e.preventDefault()
       const response = Api.post('justification', data)
       
       console.log(">>>>>>>>>",response)
       //{/window.confirm("Tem certeza?");/}
   }



    return(
        <div>
            <form className={"form"} >
            <text>Justifique sua sa√≠da</text>
                <br></br>
                <label>Nome:
                    <input onChange={e => setName(e.target.value)} placeholder="Nome" type="text"/>
                </label>
                <br></br>
                <textarea onChange={e => setJustify(e.target.value)} placeholder="Sua justificativa" rows="7" cols="77"/>
                <div>
                <button  onClick={handleSub}>Enviar</button>
            </div>
            </form>
        </div>
    )
}
