import InputMask from 'react-input-mask';
import Button from '../../../components/utils/button/button';
import {useState,useEffect}from 'react';
import Api from '../../../services/api';
export default function Confirmation() {

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [crm, setCrm] = useState('');
  const [cpf, setCpf] = useState('');
  const [phone, setPhone] = useState('');
  const [specialization, setSpecialization] = useState('');
    
  useEffect(() => {
    async function jsonUser(){
      const  objt = sessionStorage.getItem('user');
      const j = JSON.parse(objt);
      
      
      setName(j.name);
      setEmail(j.email);
      setCrm(j.crm)
      setCpf(j.cpf);
      setPhone(j.phone);
      setPassword(j.password);
      setSpecialization(j.specialization);
    }
    jsonUser();
  },[])

    
   
    
    async function submitOn(e) {
        e.preventDefault();
        const data = {
          name,
          email,
          password,
          crm,
          cpf,
          phone,
          specialization
        }
        try {
          if(name!==''&&email!==''&&password!==''&&crm!==''&&phone!==''&&specialization!==''){ 
          
            const user = sessionStorage.setItem('user',JSON.stringify(data));
            
            const response = await Api.post('verifyUser',user)
            if(response.status === 201){
              console.log(user)
            }

          }else{
            alert("Fill in all data")
          }

        } catch (error) {
        const { data } = error;
        alert(data.message);
        window.location.href='/register'
        
        }
        
    }
    return (
    <div>
       <div><a href="/"><img src="https://i.imgur.com/tDvwyyA.png" className={'logo2'} title="source: imgur.com" /></a></div>
    <form>
      <h1 className='title'>Deseja alterar os dados?</h1>
      
      <div className = {"question"}>
      <input name="name" id="name" type="text" required
          value={name}
          onChange={e => setName(e.target.value)}
        />
        <label>Name:</label>
      </div>
      <br></br>
      <div className = {"question"}>
        <InputMask mask="999.999.999-99" name="cpf" type="text" id="cpf" required
        value={cpf}
        onChange={e => setCpf(e.target.value)}
        
        />
        
        <label>CPF:</label>
      </div>
      <br></br>
      <div className={"question"}>
        <InputMask mask="9999" id="crm" name="crm" type="text" required

        value={crm}
        onChange={e => setCrm(e.target.value)}
        />
       <label>CRM:</label>
      </div>
      <br></br>
      <div className={"question"}>
        <input name="specialization" id="crm" type="text" required
          value={specialization}
          onChange={e => setSpecialization(e.target.value)}
        />
        <label>Specialization:</label>
      </div>
      <br></br>
      <div className={"question"}>
        <InputMask mask="(99) 99999-9999" id="phone" name="phone" type="text" required
            value={phone}
            onChange={e => setPhone(e.target.value)}
        />
        <label>Phone:</label>   
      </div>
      <br></br>
        <div className={"question"}>
          <input name="email" id="email" type="text" required
           value={email}
           onChange={e => setEmail(e.target.value)}
           />
          <label>E-mail:</label>
        </div>
        <br></br>
        <div className={"question"}>
          <input mask="password" id="password" name="password" type="text" required
            value={password}
            onChange={e => setPassword(e.target.value)}
        />
        <label>Password:</label>
      </div>
      <br></br>
      <div>
      
      <Button type="submit" onClick={(submitOn)}>submit</Button>
    </div>  
    </form>
            
        </div>
    )

}
