import React,{useState} from 'react';
import { createStyles, Theme, makeStyles } from '@material-ui/core/styles';
import FilledInput from '@material-ui/core/FilledInput';
import FormControl from '@material-ui/core/FormControl';
import FormHelperText from '@material-ui/core/FormHelperText';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import Api from '../../services/api';

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function Register() {

  const classes = useStyles();
  const [cpf, setCpf] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [phone2, setPhone2] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirm] = useState('');
  const [crm, setCrm] = useState('');
  const [name, setName] = useState('');
  const [specialization, setSpecialization] = useState('');
  const [checked, setChecked] = useState('');
  const [loading, setLoading] = useState(false) 
 

  async function submitOn(e) {

    setLoading(true);
    e.preventDefault();
    const data = {
      name: name,
      email: email,
      password: password,
      crm: crm,
      cpf: cpf,
      phone: phone,
      phone2:phone2,
      specialization: specialization
    }
    
    sessionStorage.setItem('emailValidator', email);
    try {
      if(name!==''&&email!==''&&password!==''&&crm!==''&&phone!==''&&specialization!==''){
        
        if(password !== confirmPassword){
          alert("password other than confirm password")
        }

        if(password === confirmPassword){
          const response = await Api.post('verifyUser',data);
          if(response.status === 201){
          sessionStorage.setItem('user',JSON.stringify(data));
          window.location.href= '/confirmation';
        }
        }
        setLoading(false);
      }else{
        alert("Fill in all data")
        setLoading(false);
      }
      
    
    } catch (error) {
      const { data } = error.response;
      alert(data.message);
      
    }
        
      
    
}

function textChange(e){
  
  setChecked({input: e.target.checked})
}
function onblurCrm(e){
  
  const {value} = e.target;

  if(value === ''){
    return;
  }
  fetch(`https://www.consultacrm.com.br/api/index.php?tipo=crm&uf=am&q=${value}&chave=2798018964&destino=json`)
  .then(data => data.json())
  .then(data =>{
    for(const pessoa of data.item){
      setName(pessoa.nome)
      if(pessoa.profissao === ''){
        setSpecialization("Nenhuma");
      }else{
        setSpecialization(pessoa.profissao);
      }
      
    }
  })
  
}

function senhaForca(){
  var senha = document.getElementById('password').value;
  var forca = 0;
  document.getElementById("impSenha").innerHTML = "Password Must be at least 6 to 13 characters long"

  if((senha.length >=4) && (senha.length <= 7)){
    forca += 10;
  }else if(senha.length >7){
    forca += 25
  }
  if((senha.length >= 5) && (senha.match(/[a-z]+/))){
    forca += 10;
  }
  if((senha.length >= 6) && (senha.match(/[A-Z]+/))){
    forca += 20;
  }
  if((senha.length >= 7) && (senha.match(/[@#$%&;*]/))){
    forca += 25;
  }
  mostrarForca(forca);
}

function mostrarForca(forca){
  document.getElementById("impForcaSenha").innerHTML = "força: " + forca;
  if(forca < 30){
    document.getElementById("erroSenhaForca").innerHTML = "<span style='color: #ff0000'>Fraca</span>";
  }else if((forca >= 30) && (forca < 60)){
    document.getElementById("erroSenhaForca").innerHTML = "<span style='color: #FFD700'>Média</span>";
  }else if((forca >= 50) && (forca<70)){
    document.getElementById("erroSenhaForca").innerHTML = "<span style='color: #7FFF00'>Forte</span>";
  }else if((forca >= 70) && (forca<100)){
    document.getElementById("erroSenhaForca").innerHTML = "<span style='color: #008000'>Excelente</span>";
  }
}

function loadingSubmit(){
  setLoading(true);
  setTimeout(
    2000,
  )
}

return (
  <div>
  <div><a href="/"><img src="https://i.imgur.com/tDvwyyA.png" className={'logo2'} title="source: imgur.com" /></a></div>
  <form className={classes.root} noValidate autoComplete="off">
      <FormControl>
        <InputLabel htmlFor="component-simple">Name</InputLabel>
        <Input id="component-simple" value={name} />
      </FormControl>
      <FormControl>
        <InputLabel htmlFor="component-helper">Name</InputLabel>
        <Input
          id="component-helper"
          value={name}
      
          aria-describedby="component-helper-text"
        />
        <FormHelperText id="component-helper-text">Some important helper text</FormHelperText>
      </FormControl>
      <FormControl error>
        <InputLabel htmlFor="component-error">Name</InputLabel>
        <Input
          id="component-error"
          value={name}
          
          aria-describedby="component-error-text"
        />
        <FormHelperText id="component-error-text">Error</FormHelperText>
      </FormControl>
      <FormControl variant="outlined">
        <InputLabel htmlFor="component-outlined">Name</InputLabel>
        <OutlinedInput id="component-outlined" value={name}  label="Name" />
      </FormControl>
      <FormControl variant="filled">
        <InputLabel htmlFor="component-filled">Name</InputLabel>
        <FilledInput id="component-filled" value={name}  />
      </FormControl>
    </form>
  </div>
);
}
