import { useState} from "react";
import Api from "../../services/api";
import {useParams} from 'react-router-dom';
import Button from '../../components/utils/button/button';

export default function Justification(){

   const [name, setName] = useState('');
   const [description, setDescription] = useState('');
   const [tnc, setTnc] = useState(false);

   const {idUsuario} = useParams();
   
   async function handleSub(e){

    e.preventDefault()
        

        const config = {
            headers:{
                Authorization:localStorage.getItem('token')
        }

    }
        try {
            var listaMarcados = document.getElementsByTagName("INPUT");
            for (var loop = 0; loop < listaMarcados.length; loop++) {
                var item = listaMarcados[loop];
                if (item.type == "checkbox" && item.checked) {
                  console.log(item.value+',');
                  
                }
             }
             console.log(name+',');
             console.log(description+',');
        } catch (error) {
            console.log(">>>>>ERRO>>>>",error)
        }
       
   }



   return(
    <div>
        <div><a href="/home"><img src="https://i.imgur.com/tDvwyyA.png" className = {'logo'} title="Smart Palm"/></a></div><br></br><br></br>
        <form className={"form"} >
        <text className={"subtitle"}>Justifique sua saída:</text>
            <br></br><br></br>
            <label className={"container"}>eu Não usarei mais a plataforma
            <input type = "checkbox"></input>
            <span className={"checkmark"}></span>
            </label><br></br><br></br>
            <label className={"container"}>eu Não atendeu as minhas necessidades
            <input type = "checkbox"></input>
            <span className={"checkmark"}></span>
            </label><br></br><br></br>
            <label className={"container"}>eu Achei a plataforma confusa ou difícil
            <input type = "checkbox"></input>
            <span className={"checkmark"}></span>
            </label><br></br><br></br>
            <label className={"container"}>eu Simplesmente não gostei da plataforma
            <input type = "checkbox"></input>
            <span className={"checkmark"}></span>
            </label><br></br><br></br>
            <label className={"container"}>eu Não consegui utilizar as funcionalidades
            <input type = "checkbox"></input>
            <span className={"checkmark"}></span>
            </label><br></br><br></br>
            <label className={"container"}>eu Outros motivos:
            <input type = "checkbox" id="status" onChange={Status()}></input>
            <span className={"checkmark"}></span>
            </label><br></br><br></br>
            <textarea className = {"textarea"} id="estado" onChange={Status()} placeholder="Insira sua justificativa"></textarea>
            <div>
             <br></br>
            <Button>Enviar</Button>
            </div>
        </form>
    </div>
)
}
