import {useState} from 'react';
import Api from '../../services/api';
import Button from '@material-ui/core/Button';
import './form.css';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Divider from '@material-ui/core/Divider';
import CircularProgress from '@material-ui/core/CircularProgress';


const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '60ch',
      },
    },
  }));
export default function ResetPassword() {

    const classes = useStyles();

    const [email, setEmail] = useState('')
    const [loading, setLoading] = useState(false) 

    async function handleSubmmit(e){
        e.preventDefault()
        
            try {
                if (email!=='') {
                    const res = await Api.post('emailUpdate', {
                        email
                    })
                    setLoading(false);
                    alert("We send an email with the password reset link to the registered email, access your email to change the password!")
                    window.location.href = '/'
                    console.log(">>>>>>>>>>>>>", res)
                }else{
                    alert("Fill in all fields")
                }
                setLoading(false);
            } catch (error) {
                const { data } = error.response;
                alert(data.message);
                window.location.href='/'
            }          
    }
    function loadingSubmit(){
        setLoading(true);
        setTimeout(
          () => handleSubmmit(),
          2000
        )
      }
        return (
        <form   className={classes.root} noValidate autoComplete="off">
            <br/>
            <text >Forgot Password</text>
            <br/>
            <TextField
            id="outlined-basic" 
            fullWidth label="Email" 
            type="email"
            onChange={e => setEmail(e.target.value)}
            variant="outlined" />
            <br/>
            
            <Divider variant="middle"/>
            <br/>
            <div>
            
            <Button 
                fullWidth 
                variant="contained"
                onClick={loadingSubmit} 
                disable={loading}    
                color="primary">
                {loading?<CircularProgress color="inherit" />:"Send"}
            </Button>
            </div>
            <br/>
        </form>
        );
    }
