import React from 'react';
import Botao from './botao';


export default function Clock() {

   
    
      
    
  //document.getElementById("btn").setAttribute("disabled", "disabled");



      function handleContadorplus(){
        if (this.state.estado==="Iniciar"){
          this.setState({
            estado: "Pausa"
          });
          this._interval=setInterval(this.aumentar,1000);
          }
        else if (this.state.estado==="Pausa"){
            this.setState({
              estado: "Iniciar"
          });
          clearInterval(this._interval);
         
        }
    
      }
      function aumentar(){
        if(this.state.estado==="Pausa"){
          this.setState({
            contador: this.state.contador-1
            
          });
          if(this.state.contador === 0){
            window.location.href= '/';
        }
        }
      }
      function handleContadorminus(){
        this.setState({
          contador: 0,
          estado:"Iniciar"
        });
        clearInterval(this._interval);
      }
      
      
        return (
          <div className=" text-center">
    
            <h1>{this.state.contador}</h1>
            <input Botao id='btn' text={this.state.estado}   onClick={this.handleContadorplus}/>
            <Botao text="Detener"  onClick={this.handleContadorminus}/>
            <Botao text="outro"  onClick={this.aumentar}/>
          </div>
        );
      
    }
