                    console.log(">>>>>>>>>>>>>", res)
                }else{
                    alert("Fill in all fields")
                }
             
            } catch (error) {
                const { data } = error.response;
                alert(data.message);
                window.location.href='/'
            }          
    }
    
        return (
        <div>
        <form className={classes.root} noValidate autoComplete="off">
