import {useState} from 'react';
import 'react-toastify/dist/ReactToastify.css';
import InputMask from 'react-input-mask';
import '../register/style.css';
import Button from '@material-ui/core/Button';
import Api from '../../services/api';
import AlertDialog from '../../components/utils/dialog/alertDialog.js'
import CircularProgress from '@material-ui/core/CircularProgress';


export default function Register() {

  const [cpf, setCpf] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [phone2, setPhone2] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirm] = useState('');
  const [crm, setCrm] = useState('');
  const [name, setName] = useState('');
  const [specialization, setSpecialization] = useState('');
  const [checked, setChecked] = useState('');
  const [loading, setLoading] = useState(false) 
 

  async function submitOn(e) {

    setLoading(true);
    e.preventDefault();
    const data = {
      name: name,
      email: email,
      password: password,
      crm: crm,
      cpf: cpf,
      phone: phone,
      phone2:phone2,
      specialization: specialization
    }
    
    sessionStorage.setItem('emailValidator', email);
    try {
      if(name!==''&&email!==''&&password!==''&&crm!==''&&phone!==''&&specialization!==''){
        
        if(password !== confirmPassword){
          alert("password other than confirm password")
        }

        if(password === confirmPassword){
          const response = await Api.post('verifyUser',data);
          if(response.status === 201){
          sessionStorage.setItem('user',JSON.stringify(data));
          window.location.href= '/confirmation';
        }
        }
        setLoading(false);
      }else{
        alert("Fill in all data")
        setLoading(false);
      }
      
    
    } catch (error) {
      const { data } = error.response;
      alert(data.message);
      
    }
        
      
    
}

function textChange(e){
  
  setChecked({input: e.target.checked})
}
function onblurCrm(e){
  
  const {value} = e.target;

  if(value === ''){
    return;
  }
  fetch(`https://www.consultacrm.com.br/api/index.php?tipo=crm&uf=am&q=${value}&chave=2798018964&destino=json`)
  .then(data => data.json())
  .then(data =>{
    for(const pessoa of data.item){
      setName(pessoa.nome)
      if(pessoa.profissao === ''){
        setSpecialization("Nenhuma");
      }else{
        setSpecialization(pessoa.profissao);
      }
      
    }
  })
  
}

function senhaForca(){
  var senha = document.getElementById('password').value;
  var forca = 0;
  document.getElementById("impSenha").innerHTML = "Password Must be at least 6 to 13 characters long"

  if((senha.length >=4) && (senha.length <= 7)){
    forca += 10;
  }else if(senha.length >7){
    forca += 25
  }
  if((senha.length >= 5) && (senha.match(/[a-z]+/))){
    forca += 10;
  }
  if((senha.length >= 6) && (senha.match(/[A-Z]+/))){
    forca += 20;
  }
  if((senha.length >= 7) && (senha.match(/[@#$%&;*]/))){
    forca += 25;
  }
  mostrarForca(forca);
}

function mostrarForca(forca){
  document.getElementById("impForcaSenha").innerHTML = "força: " + forca;
  if(forca < 30){
    document.getElementById("erroSenhaForca").innerHTML = "<span style='color: #ff0000'>Fraca</span>";
  }else if((forca >= 30) && (forca < 60)){
    document.getElementById("erroSenhaForca").innerHTML = "<span style='color: #FFD700'>Média</span>";
  }else if((forca >= 50) && (forca<70)){
    document.getElementById("erroSenhaForca").innerHTML = "<span style='color: #7FFF00'>Forte</span>";
  }else if((forca >= 70) && (forca<100)){
    document.getElementById("erroSenhaForca").innerHTML = "<span style='color: #008000'>Excelente</span>";
  }
}



return (
  <div>
  <div><a href="/"><img src="https://i.imgur.com/tDvwyyA.png" className={'logo2'} title="source: imgur.com" /></a></div>
    <form >
    
      <h1 className='title'>Register</h1>
      <h1 className='title'>{loading?<CircularProgress color="secondary"/>:''}</h1>
      <h2 className='subtitle'>Fill in the information below</h2>
      <div className={"question"}>
        <InputMask mask="9999" id="crm" name="crm" type="text" required
          onBlur={onblurCrm}
          onChange={e => setCrm(e.target.value)}
        />
       <label>*CRM:</label>
      </div>
      <br></br>
      <div className = {"question"}>
      <input name="name" id="name" type="text" required
          value={name}
        />
        <label>*Name:</label>
      </div>
      <br></br>
      <div className = {"question"}>
        <InputMask mask="999.999.999-99" name="cpf" type="text" id="cpf" required
        onChange={e => setCpf(e.target.value)}
        />  
        <label>*CPF:</label>
      </div>
      <div className={"question"}>
        <input name="specialization" id="specialization" type="text" required
         value={specialization}
         
        />
        <label>*Specialization:</label>
      </div>
      <br></br>
      <div className={"question"}>
        <InputMask mask="(99)99999-9999" id="phone" name="phone" type="text" required
         onChange={e => setPhone(e.target.value)}
        />
        <label>*Phone:</label>   
      </div>
      
      <br></br>
      <div className={"question"}>
        <InputMask mask="(99)99999-9999" id="phone2" name="phone2" type="text" required
         onChange={e => setPhone2(e.target.value)}
        />
        <label>Phone2:</label>   
      </div>
      <br></br>
        <div className={"question"}>
          <input name="email" id="email" type="email" required
          onChange={e => setEmail(e.target.value)}
           />
          <label>*E-mail:</label>
        </div>
        <br></br>
        <div className={"question"}>
          <input  id="password" name="password" type="password" required
          onChange={e => setPassword(e.target.value)}
          onKeyUp={(senhaForca)}
        />
        <label>*Password:</label>
        <div id="impSenha"></div>
        <div id="erroSenhaForca"></div>
        <div id="impForcaSenha"></div>
      </div>
      
        <br></br>
        <div className={"question"}>
          <input  id="password" name="password" type="password" required
           onChange={e => setConfirm(e.target.value)}
        />
        <label>*Confirm Password:</label>
      </div>
      <br></br>    
      
      <div>
      <AlertDialog/>
      <input type="checkbox"  onChange={textChange.bind(this)} />
      <label>Concordo com os termos</label>
      <br></br>
      <br></br>
      
    </div>  
    <Button 
     fullWidth type="submit" variant="contained"
     onClick={submitOn}
      disabled={!checked.input} color="primary">Sign Up</Button>
    </form>
  </div>
);
  }
