import Button from '../../pages/login/buttonLogin/Button';
import './index.css';
import { useState} from "react";
import Api from '../../services/api';

export default function SignIn() {

  const [ email, setEmail ] = useState('');
  const [ password, setPassword ] = useState('');
 

  async function handleSubmit(){
   
    
    try {
      if(email!==''&&password!==''){
        await Api.post('signInDoctor',{email, password})
    .then(response =>{
      if(response.status === 200){
        localStorage.setItem('token', response.data.token)
        window.location.href='/home'
      }else if(response.data.status === 404){
        alert('Este usuário não existe ou está inativo')
        window.location.href='/'
      }else{
        alert('Erro no servidor')
        window.location.href='/'
      }
    })
      }else{
        alert('Por favor, preencha todos os dados!');
      }
      
    } catch (error) {
      alert("E-mail ou senha inválidos, verifique se suas credenciais são válidas!")
      window.location.href='/'
    }

    
}


return (
  <div>
  <div><a href="/home"><img src="https://i.imgur.com/tDvwyyA.png" className = {'logo'} title="Smart Palm"/></a></div>
  <br></br>
  <br></br>
  <form>
  <h1 className={"title"}>Faça seu login.</h1>
  
    <div className={"question"}>
    <input type="text" name="Email" required onChange={e => setEmail(e.target.value)} />
    <label>E-mail:</label>
    </div>
    <div className = {"question"}>
    <input type="password" name="password" required 
        onChange={e => setPassword(e.target.value)}/>
    <label>Senha:</label>
    </div>
    <br></br>
    <div className ={"direita"}><Link to ='/confirmation'><Button type = "submit" onClick={(onsubmit)}>Entrar</Button></Link></div>
    <br></br>
    <br></br>
    <a href = "/forgot" className={"label1"}>Esqueci a senha</a>
    <a href ="/register" className={"label2"}>Cadastrar-se</a>
   </form>
  
</div>
);
}
