import React,{useState,useEffect} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';

export default function FormDialog() {
  const [open, setOpen] = React.useState(false);
  const [email, setEmail] = useState('');
  
  useEffect(() => {
    async function jsonUser(){
      const  user = sessionStorage.getItem('emailValidator');
      setEmail(user);
    }
    jsonUser();
  },[])


  const handleClickOpen = () => {
    
    setOpen(true);
  };

  const handleClose = () => {
    sessionStorage.setItem('emailValidator', email)
    window.location.href= '/confirmEmail';
    setOpen(false);
  };

  return (
    <div>
      <Button fullWidth variant="outlined" color="secondary" onClick={handleClickOpen}>
      To edit
      </Button>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Editar E-mail</DialogTitle>
        <DialogContent>
          
          <TextField
            autoFocus
            margin="dense"
            id="name"
            value={email}
            onChange={e => setEmail(e.target.value)}
            label="Email"
            type="email"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleClose} color="primary">
            ok
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
