import {useParams} from 'react-router-dom';
import {useState}from 'react';
import Api from '../../services/api';
import Button from '../../components/utils/button/button'
import InputMask from 'react-input-mask';

export default function ProfilePerfil() {

    const [specialization, setSpecialization] =  useState('');
    const [phone, setPhone] =  useState('');

    const {idUsuario} = useParams();
    
    

async function handleSubmit(e){
    e.preventDefault();
    const data = {
        specialization:specialization,
        phone:phone,}


    const config = {
        headers:{
            Authorization:localStorage.getItem('token')
    }
    
}
try {
    if(specialization!==''&&phone!==''){
        const response = await Api.put(`updateUser/${idUsuario}`,data, config);

        if(response.status===200){
          window.location.href='/viewProfile'
        }else{
          alert('Erro ao atualizar o usuário!');
        }
      }else{
        alert('Por favor, preencha todos os dados!');
      }
} catch (error) {
    console.log(">>>>>>>>>>>>>>>>>>>>>", error)
}
   

}
    
        return(
            <div>
               <div><a href="/home"><img src="https://i.imgur.com/tDvwyyA.png" className = {'logo'} title="Smart Palm"/></a></div>
                    <form>
                    <text className = {'title'}>Editar Perfil</text>
                    <br/>
                    <div className={"question"}>
                        <input name="specialization" id="crm" type="text" required
                            onChange={e => setSpecialization(e.target.value)}
                            />
                        <label>Specialization:</label>
                    </div>
                        
                     <div className={"question"}>
                         <InputMask mask="(99) 99999-9999" id="phone" name="phone" type="text" required
                            onChange={e => setPhone(e.target.value)}
                            />
                        <label>Phone:</label>   
        
                        </div>
                        <br></br>
                        <Button onClick={handleSubmit} buttonSize='btn--large' type="submit">Editar</Button>
                
                    </form>
                    
            </div>
        )
}
