import React,{useState,useEffect} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import Api from '../../../../services/api';

export default function FormDialog() {
  const [open, setOpen] = React.useState(false);
  const [email, setEmail] = useState('');

  useEffect(() => {
    async function jsonUser(){
      const  user = sessionStorage.getItem('emailValidator');
      setEmail(user);
    }
    jsonUser();
  },[])


  async function handleClickOpen (e) {

  const emailExists = sessionStorage.getItem('emailValidator')
  console.log("email",JSON.parse( emailExists))
   const data ={
     email: emailExists
   }
   const response = await Api.post('findEmail/',data);
   if(response.status === 200){
    
    console.log(">>>>>>>>",response.data.id)
    const dataId = sessionStorage.setItem('id',response.data.id);
    const dataId2 = sessionStorage.getItem('id');
    console.log("id>",dataId2)
  }
   console.log(data)



    setOpen(true);
  };

  async function okHandle (e) {
    e.preventDefault();
    
    sessionStorage.setItem('emailValidator', JSON.stringify(email))
    const b = sessionStorage.getItem('emailValidator');
    console.log("newEmail",email)
    console.log("b",b)
    const dataId = sessionStorage.getItem('id');
    const response = await Api.put(`updateMailSend/${dataId}`, {email});
    setOpen(false);
  };
 

  return (
    <div>
      <Button fullWidth variant="contained" color="secondary" onClick={handleClickOpen}>
      To edit
      </Button>
      <Dialog open={open} type="email" onClose={okHandle} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Editar E-mail</DialogTitle>
        <DialogContent>
          
          <TextField
            autoFocus
            margin="dense"
            id="name"
            value={email}
            onChange={e => setEmail(e.target.value)}
            label="Email"
            type="email"
            require
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          
          <Button
          
          onClick={okHandle} color="secondary">
            ok
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
