import {useState} from 'react';
import Button from '../../components/utils/button/button';
import {useParams} from 'react-router-dom';
import InputMask from 'react-input-mask';
import Api from '../../services/api';

export default function ConfirmPassword(){

    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirm] = useState('');
    const {idUsuario} = useParams();
    
    async function handleSubmmit(e){
        e.preventDefault();
        const data = {
            password:password,
        }
        try {

            if(password!=='' && confirmPassword!==''){
                if(password === confirmPassword){
                   await Api.put(`updatePassword/${idUsuario}`,data)
                   alert('Senha alterada com sucesso!');
                   window.location.href='/'
                }else{
                 alert('Password is different from password confirmation!');
                }
            }else{
                alert('Please !, fill in all the data!');
            }

        } catch (error) {
            const { data } = error.response;
            alert(data.message);
            
        }
       
       
    }

    return (
        <div>
            <div><a href="/"><img src="https://i.imgur.com/tDvwyyA.png" className = {'logo'} title="Smart Palm"/></a></div>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <form className={'form'}>
            <text>Forgot Password</text>
            <br></br>
            <label>
                <br></br>
                New Password:
                <input  type="password" onChange={e => setPassword(e.target.value)} />
            </label>
            <br></br>
            <label>
                <br></br>
                Confirm Password:
                <input type="password" onChange={e => setConfirm(e.target.value)} />
            </label>
            <Button buttonSize='btn--large' onClick={handleSubmmit}>SUBMIT</Button>
          </form>
          
        </div>
    );
}
