import { useState} from "react";
import Api from "../../services/api";
import {useParams} from 'react-router-dom';


export default function Justification(){

   const [name, setName] = useState('');
   const [description, setDescription] = useState('');

   const {idUsuario} = useParams();
   
   async function handleSub(e){

    e.preventDefault()
        

        const config = {
            headers:{
                Authorization:localStorage.getItem('token')
        }

    }
        try {
            
        if(name!==''&&description!==''){
            await Api.post('justification',{name, description}, config)
            window.location.href= `/enableProfile/${idUsuario}`;
       //{/window.confirm("Tem certeza?");/}
        }else{
            alert('Preencha todos os campos')
        }
        
        } catch (error) {
            console.log(">>>>>ERRO>>>>",error)
        }
       
   }



    return(
        <div>
            <form className={"form"} >
            <text>Justifique sua sa√≠da</text>
                <br></br>
                <label>Nome:
                    <input onChange={e => setName(e.target.value)} placeholder="Nome" type="text"/>
                </label>
                <br></br>
                <textarea onChange={e => setDescription(e.target.value)} placeholder="Sua justificativa" rows="7" cols="77"/>
                <div>
                <button  onClick={handleSub}>Enviar</button>
            </div>
            </form>
        </div>
    )
}
