import { useState} from "react";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Input from '../../components/utils/regex/input';
import Api from '../../services/api';
import '../register/style.css';
import Button from '../../components/utils/button/button'

toast.configure();

export default function Register() {

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [crm, setCrm] = useState('');
  const [cpf, setCpf] = useState('');
  const [phone, setPhone] = useState('');
  const [specialization, setSpecialization] = useState('');

  async function submitOn() {
    const data = {
      name: name,
      email: email,
      password: password,
      crm: crm,
      cpf: cpf,
      phone: phone,
      specialization: specialization
    }
    console.log(">>>>>>>>>>>>>>>>>>>>>>",data);

    try {
      if(name!==''&&email!==''&&password!==''&&crm!==''&&phone!==''&&specialization!=''){
        const response = await Api.post('doctorSignUp',data);
  
        if(response.status===201){
          alert('Verifique seu e-mail para confirmar o cadastro');
          window.location.href= '/';
        }else{
          alert('Erro ao cadastrar o usu치rio!');
          window.location.href= '/register';
        }
      }else{
        alert('Por favor, preencha todos os dados!');
      }
    } catch (error) {
      alert('Erro ao cadastrar o usu치rio! '+error);
      window.location.href= '/register';
    }
    
    /*try {
      const response = await Api.post('doctorSignUp', data);
    if(response.status === 201){
      alert('Verifique seu e-mail para confirmar o cadastro');
      window.location.href= '/';
    }else if(response.status === 401){
      alert('Verifique o crm');
    }
    } catch (error) {
      alert('Erro no servidor!');
    }
    */
}


return (
  <div>
  <div><a href="/home"><img src="https://i.imgur.com/tDvwyyA.png" className={'logo2'} title="source: imgur.com" /></a></div>
  <div className='Principal'>
  <h1 className='title'>Register</h1>
  <h2 className='subtitle'>Fill in the information below</h2>
  </div>
  <br></br>
  <br></br>
      
    <form className={"divCenter"}>
      <div>
      <label className ={'subtitle'}>User Informations</label><br></br><br></br>
      <label className={"text"}>Name:</label>
        <br></br>
        <Input className = {'input'} name="name" id="name" type="text"
          onChange={e => setName(e.target.value)}
        />
        <label className={"text"}>CPF:
        <Input className = {'input'} mask="cpf" name="cpf" type="text" id="cpf"

          onChange={e => setCpf(e.target.value)}
        />
        </label>
       <label className={"text"}>CRM:
        <Input className = {'input'} mask="crm" id="crm" name="crm" type="text"

          onChange={e => setCrm(e.target.value)}
        />
        </label>
        <label className={"text"}>Specialization:
        <Input className = {'input'} name="specialization" id="crm" type="text"

          onChange={e => setSpecialization(e.target.value)}
        />
        </label>
        <label className={"text"}>Phone:
        <Input className = {'input'} mask="telefone" id="phone" name="phone"
          onChange={e => setPhone(e.target.value)}
        />
        </label>
          
      </div>  
      <br></br><br></br>
      <div>
        <label className ={'subtitle'}>Login Informations</label><br></br><br></br>
        <label className={"text"}>E-mail:
        <Input className = {'input'} name="email" id="email" type="email"
          onChange={e => setEmail(e.target.value)}
           />
        </label>

      </div>      
      
        <label className={"text"}>Password:
        <Input className = {'input'} mask="password" id="password" name="password" type='password'

          onChange={e => setPassword(e.target.value)}
        />
        </label>
        <br></br>
        
    </form>
    <div className={"divCenter"}>
    <Button buttonSize='btn--large' type="submit" onClick={(submitOn)}>Enviar</Button>
    </div>
    
  </div>
);
}
