import { useState} from "react";
import Api from "../../services/api";
import {useParams} from 'react-router-dom';
import Button from '../../components/utils/button/button';

export default function Justification(){

   const [name, setName] = useState('');
   const [description, setDescription] = useState('');
   const [tnc, setTnc] = useState(false);

   const {idUsuario} = useParams();
   
   async function handleSub(e){

    e.preventDefault()
        

        const config = {
            headers:{
                Authorization:localStorage.getItem('token')
        }

    }
        try {
        var els = document.querySelectorAll("input[type='checkbox'");
        var selecionados = '';
        for (var i = 0; i < els.length; i++) {
            if (els[i].checked) {
                selecionados += els[i].nextSibling.nodeValue + ',';
                document.getElementById("sel").innerHTML = selecionados.substring(0, selecionados.length - 1);
            }
        console.log(els)
        }
    
    } catch (error) {
            console.log(">>>>>ERRO>>>>",error)
        }
       
   }



    return(
        <div>
            <form className={"form"} >
            <text className={"subtitle"}>Justifique sua saída</text>
                <br></br><br></br>
                <div className={"question"}>
                <input name="name" id="name" type="text" required
                    onChange={e => setName(e.target.value)}
                    />
                <label>Name:</label>
             </div>
             <div>
                 <input id="ok"  name="tnc" onChange={e=>setTnc(e.target.checked)} type="checkbox"/><span>Não usarei mais a plataforma</span>
                 <br></br>
             </div>

                <br></br>
                <textarea className = {"textarea"} onChange={e => setDescription(e.target.value)} placeholder="Sua justificativa" rows="7" cols="77"/>
                <div>
                    <br></br>
                <Button  onClick={handleSub}>Enviar</Button>
            </div>
            </form>
        </div>
    )
}
