import React,{useState} from 'react';
import 'react-toastify/dist/ReactToastify.css';
import InputMask from 'react-input-mask';
import '../register/style.css';
import Button from '@material-ui/core/Button';
import Api from '../../services/api';
import AlertDialog from '../../components/utils/dialog/alertDialog.js'
import CircularProgress from '@material-ui/core/CircularProgress';
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    '& > * + *': {
      marginLeft: theme.spacing(2),
    },
  },
}));
export default function Register() {

  const [cpf, setCpf] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [phone2, setPhone2] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirm] = useState('');
  const [crm, setCrm] = useState('');
  const [name, setName] = useState('');
  const [specialization, setSpecialization] = useState('');
  const [checked, setChecked] = useState('');
  const [loading, setLoading] = useState(false) 
 

  async function submitOn(e) {

    setLoading(true);
    e.preventDefault();
    const data = {
      name: name,
      email: email,
      password: password,
      crm: crm,
      cpf: cpf,
      phone: phone,
      phone2:phone2,
      specialization: specialization
    }
    
    console.log(">>>>>>>>>>>",setChecked)
    try {
      if(name!==''&&email!==''&&password!==''&&crm!==''&&phone!==''&&specialization!==''){
        
        if(password !== confirmPassword){
          alert("password other than confirm password")
        }

        if(password === confirmPassword){
          const response = await Api.post('verifyUser',data);
          if(response.status === 201){
          sessionStorage.setItem('user',JSON.stringify(data));
          window.location.href= '/confirmation';
        }
        }
        setLoading(false);
      }else{
        alert("Fill in all data")
        setLoading(false);
      }
      
    
    } catch (error) {
      const { data } = error.response;
      alert(data.message);
      
    }
        
      
    
}

function textChange(e){
  setChecked({input: e.target.checked})
}
function onblurCrm(e){
  
  const {value} = e.target;

  if(value === ''){
    return;
  }
  fetch(`https://www.consultacrm.com.br/api/index.php?tipo=crm&uf=am&q=${value}&chave=2798018964&destino=json`)
  .then(data => data.json())
  .then(data =>{
    for(const pessoa of data.item){
      setName(pessoa.nome)
      setSpecialization(pessoa.profissao)
    }
  })
  
}

function loadingSubmit(){
  setLoading(true);
  setTimeout(
    2000,
  )
}

return (
  <div>
  <div><a href="/"><img src="https://i.imgur.com/tDvwyyA.png" className={'logo2'} title="source: imgur.com" /></a></div>
    <form onSubmit={submitOn}>
    {loading?<CircularProgress color="secondary"/>: "Sign Up"}
      <h1 className='title'>Register</h1>
      <h2 className='subtitle'>Fill in the information below</h2>
      <div className={"question"}>
        <InputMask mask="9999" id="crm" name="crm" type="text" required
          onBlur={onblurCrm}
          onChange={e => setCrm(e.target.value)}
        />
       <label>*CRM:</label>
      </div>
      <br></br>
      <div className = {"question"}>
      <input name="name" id="name" type="text" required
          value={name}
        />
        <label>*Name:</label>
      </div>
      <br></br>
      <div className = {"question"}>
        <InputMask mask="999.999.999-99" name="cpf" type="text" id="cpf" required
        onChange={e => setCpf(e.target.value)}
        />  
        <label>*CPF:</label>
      </div>
      <div className={"question"}>
        <input name="specialization" id="crm" type="text" required
         value={specialization}
         onChange={e => setSpecialization(e.target.value)}
        />
        <label>*Specialization:</label>
      </div>
      <br></br>
      <div className={"question"}>
        <InputMask mask="(99) 99999-9999" id="phone" name="phone" type="text" required
         onChange={e => setPhone(e.target.value)}
        />
        <label>*Phone:</label>   
      </div>
      
      <br></br>
      <div className={"question"}>
        <InputMask mask="(99) 99999-9999" id="phone2" name="phone2" type="text" required
         onChange={e => setPhone2(e.target.value)}
        />
        <label>Phone2:</label>   
      </div>
      <br></br>
        <div className={"question"}>
          <input name="email" id="email" type="email" required
          onChange={e => setEmail(e.target.value)}
           />
          <label>*E-mail:</label>
        </div>
        <br></br>
        <div className={"question"}>
          <input  id="password" name="password" type="password" required
          onChange={e => setPassword(e.target.value)}
          
        />
        <label>*Password:</label>
      </div>
      
        <br></br>
        <div className={"question"}>
          <input  id="password" name="password" type="password" required
           onChange={e => setConfirm(e.target.value)}
        />
        <label>*Confirm Password:</label>
      </div>
      <br></br>    
      
      <div>
      <AlertDialog/>
      <input type="checkbox"  onChange={textChange.bind(this)} />
      <label>Concordo com os termos</label>
      <br></br>
      <br></br>
      
    </div>  
    <Button 
     fullWidth type="submit" variant="contained"
     onClick={loadingSubmit}
      disabled={!checked.input} color="primary">Sign Up</Button>
    </form>
    
    
  </div>
);
  }
