import Button from '@material-ui/core/Button';
import './index.css';
import { useState} from "react";
import Api from '../../services/api';
import CircularProgress from '@material-ui/core/CircularProgress';

export default function SignIn() {

  const [ email, setEmail ] = useState('');
  const [ password, setPassword ] = useState('');
  const [loading, setLoading] = useState(false) 

  async function handleSubmit(){
    
    
    
    try {
      if(email!==''&&password!==''){
        await Api.post('signInDoctor',{email, password})
    .then(response =>{
      if(response.status === 200){
        localStorage.setItem('token', response.data.token)
        window.location.href='/home'
      }else if(response.data.status === 404){
        alert('This user does not exist or is inactive')
        window.location.href='/'
      }else{
        alert('server error')
        window.location.href='/'
      }
    })
    setLoading(false);
      }else{
        alert('Please !, fill in all the data!');
      }
      setLoading(false);
    } catch (error) {
      const { data } = error.response;
      alert(data.message);
      window.location.href='/'
    }
    
    
}

function loadingSubmit(){
  setLoading(true);
  setTimeout(
    () =>handleSubmit(),
    2000
  )
}
return (
  <div>
  <div><a href="/home"><img src="https://i.imgur.com/tDvwyyA.png" className = {'logo'} title="Smart Palm"/></a></div>
  <br></br>
  <br></br>
  <form >
  <h1 className={"title"}>Start your login.</h1>
    
    <div className={"question"}>
    <input type="text" name="Email" required onChange={e => setEmail(e.target.value)} />
    <label>E-mail:</label>
    </div>
    <div className = {"question"}>
    <input type="password" name="password" required 
        onChange={e => setPassword(e.target.value)}/>
    <label>Password:</label>
    </div>
    <br></br>
    <div className ={"direita"}><Button 
    type="submit"
    variant="contained"
    onClick={loadingSubmit} 
    disable={loading}
    color="primary"
    fullWidth >{loading?<CircularProgress />:"Login"}</Button></div>
    <br></br>
    <br></br>
    <a href = "/forgotPassword" className={"label1"}>I forgot the password</a>
    <a href ="/register" className={"label2"}>Sign Up</a>
   </form>
  
</div>
);
}
