import Api from "../../services/api";
import  { Component } from 'react';
import Button from '../../components/utils/button/button';

export default class Justification extends Component {

    state={
        data:{
            teste1:false,
            teste2:false,
            teste3:false,
            teste4:false,
            teste5:false,
            teste6:false,
        }
    }

   
   
   handleSub = (e)=>{

    e.preventDefault()
    var {name,checked} = e.target;

    this.setState((e)=>{
        var selected = e.data;
        return selected[name]=checked;
    })
        
       
   }


render(){
    var display=Object.keys(this.state.data);
    return(
        <div>
            <form className={"form"} >
            <text className={"subtitle"}>Justifique sua saída</text>
               
             <center>
            <br></br>
             <input name="teste1"  onChange={this.handleSub} type="checkbox"/>Não usarei mais a plataforma&nbsp;&nbsp;
             <input name="teste2" onChange={this.handleSub} type="checkbox"/>Não atendeu as minhas necessidades&nbsp;&nbsp;
             <input name="teste3" onChange={this.handleSub} type="checkbox"/>Achei a plataforma confusa ou difícil&nbsp;&nbsp;
             <input name="teste4" onChange={this.handleSub} type="checkbox"/>Simplemente, não gostei da plataforma&nbsp;&nbsp;
             <input name="teste5" onChange={this.handleSub} type="checkbox"/>Não consegui utilizar as funcionalidades&nbsp;&nbsp;
             <input name="teste6" onChange={this.handleSub} type="checkbox"/>Outros motivos&nbsp;&nbsp;
             </center>
                
                {display}
                <div>
                    <br></br>
                <Button >Enviar</Button>
            </div>
            </form>
        </div>
    )
}
}
