import React,{useState} from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Api from '../../services/api';

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function Register() {

  const classes = useStyles();
  const [cpf, setCpf] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [phone2, setPhone2] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirm] = useState('');
  const [crm, setCrm] = useState('');
  const [name, setName] = useState('');
  const [specialization, setSpecialization] = useState('');
  const [checked, setChecked] = useState('');
  const [loading, setLoading] = useState(false) 
 

  async function submitOn(e) {

    setLoading(true);
    e.preventDefault();
    const data = {
      name: name,
      email: email,
      password: password,
      crm: crm,
      cpf: cpf,
      phone: phone,
      phone2:phone2,
      specialization: specialization
    }
    
    sessionStorage.setItem('emailValidator', email);
    try {
      if(name!==''&&email!==''&&password!==''&&crm!==''&&phone!==''&&specialization!==''){
        
        if(password !== confirmPassword){
          alert("password other than confirm password")
        }

        if(password === confirmPassword){
          const response = await Api.post('verifyUser',data);
          if(response.status === 201){
          sessionStorage.setItem('user',JSON.stringify(data));
          window.location.href= '/confirmation';
        }
        }
        setLoading(false);
      }else{
        alert("Fill in all data")
        setLoading(false);
      }
      
    
    } catch (error) {
      const { data } = error.response;
      alert(data.message);
      
    }
        
      
    
}

function textChange(e){
  
  setChecked({input: e.target.checked})
}
function onblurCrm(e){
  
  const {value} = e.target;

  if(value === ''){
    return;
  }
  fetch(`https://www.consultacrm.com.br/api/index.php?tipo=crm&uf=am&q=${value}&chave=2798018964&destino=json`)
  .then(data => data.json())
  .then(data =>{
    for(const pessoa of data.item){
      setName(pessoa.nome)
      if(pessoa.profissao === ''){
        setSpecialization("Nenhuma");
      }else{
        setSpecialization(pessoa.profissao);
      }
      
    }
  })
  
}

function senhaForca(){
  var senha = document.getElementById('password').value;
  var forca = 0;
  document.getElementById("impSenha").innerHTML = "Password Must be at least 6 to 13 characters long"

  if((senha.length >=4) && (senha.length <= 7)){
    forca += 10;
  }else if(senha.length >7){
    forca += 25
  }
  if((senha.length >= 5) && (senha.match(/[a-z]+/))){
    forca += 10;
  }
  if((senha.length >= 6) && (senha.match(/[A-Z]+/))){
    forca += 20;
  }
  if((senha.length >= 7) && (senha.match(/[@#$%&;*]/))){
    forca += 25;
  }
  mostrarForca(forca);
}

function mostrarForca(forca){
  document.getElementById("impForcaSenha").innerHTML = "força: " + forca;
  if(forca < 30){
    document.getElementById("erroSenhaForca").innerHTML = "<span style='color: #ff0000'>Fraca</span>";
  }else if((forca >= 30) && (forca < 60)){
    document.getElementById("erroSenhaForca").innerHTML = "<span style='color: #FFD700'>Média</span>";
  }else if((forca >= 50) && (forca<70)){
    document.getElementById("erroSenhaForca").innerHTML = "<span style='color: #7FFF00'>Forte</span>";
  }else if((forca >= 70) && (forca<100)){
    document.getElementById("erroSenhaForca").innerHTML = "<span style='color: #008000'>Excelente</span>";
  }
}

function loadingSubmit(){
  setLoading(true);
  setTimeout(
    2000,
  )
}

return (
  <div>
  <div><a href="/"><img src="https://i.imgur.com/tDvwyyA.png" className={'logo2'} title="source: imgur.com" /></a></div>
  <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
       
        <Typography component="h1" variant="h5">
          Sign in
        </Typography>
        <form className={classes.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            autoComplete="email"
            autoFocus
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
          />
          <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Remember me"
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Sign In
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
            <Grid item>
              <Link href="#" variant="body2">
                {"Don't have an account? Sign Up"}
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={8}>
       
      </Box>
    </Container>
  </div>
);
  }
