import React,{useState,useEffect} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import axios from 'axios';

export default function FormDialog() {
  const [open, setOpen] = React.useState(false);
  const [email, setEmail] = useState('');
  
  useEffect(() => {
    async function jsonUser(){
      const  user = sessionStorage.getItem('emailValidator');
      setEmail(user);
    }
    jsonUser();
  },[])


  async function handleClickOpen () {
  
  try {
    const  user = sessionStorage.getItem('emailValidator');
    axios({
      method:'GET',
      url:`http://localhost:8081/findEmail`,
      body:{
        'email':sessionStorage.getItem('emailValidator')
    }
    }).then( resp =>{
      console.log(">>>>>>>>>", resp)
    })
    
  } catch (error) {
    const { data } = error.response;
    alert(data.message); 
  }
 
    setOpen(true);
  };

  const handleClose = (e) => {
    e.preventDefault();
    sessionStorage.setItem('emailValidator', email)
    window.location.href= '/confirmEmail';
    setOpen(false);
  };
 

  return (
    <div>
      <Button fullWidth variant="contained" color="secondary" onBlur={handleClickOpen}>
      To edit
      </Button>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Editar E-mail</DialogTitle>
        <DialogContent>
          
          <TextField
            autoFocus
            margin="dense"
            id="name"
            value={email}
            onChange={e => setEmail(e.target.value)}
            label="Email"
            type="email"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          
          <Button
          
          onClick={handleClose} color="secondary">
            ok
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
