import {useState} from 'react';
import Api from '../../services/api';
import Button from './button/button';

export default function ResetPassword() {


    const [email, setEmail] = useState('')

    async function handleSubmmit(e){
        e.preventDefault()
        
            try {
                if (email!=='') {
                    const res = await Api.post('emailUpdate', {
                        email
                    })

                    alert("We send an email with the password reset link to the registered email, access your email to change the password!")
                    window.location.href = '/'
                    console.log(">>>>>>>>>>>>>", res)
                }else{
                    alert("Fill in all fields")
                }
             
            } catch (error) {
                const { data } = error.response;
                alert(data.message);
                window.location.href='/'
            }          
    }
    
        return (
            <div>
            <div><a href="/"><img src="https://i.imgur.com/tDvwyyA.png" className = {'logo'} title="Smart Palm"/></a></div>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <form className={'form'}>
            <text>Forgot Password</text>
            <br></br>
            <label>
                <br></br>
                E-mail:
                <input type="text" onChange={e => setEmail(e.target.value)} />
            </label>
            <br></br>
            <br></br>
            <br></br>
            <Button buttonSize='btn--large' onClick={handleSubmmit}>Enviar</Button>
          </form>
          
        </div>
          
        );
    }
